import React, { useEffect, useState } from 'react';

export const ListPatients = () => {
 // const { pacientes, setPacientes } = props;

  const [pacientes, setPacientes] = useState([]); 
  const [content, setContent] = useState("");

  useEffect(() => {
    async function getPacientes() {
      const res = await fetch("/api/pacientes");
      const pacientes = await res.json();
      setPacientes(pacientes);

    }
    getPacientes();


  }, []);

  const deletePaciente = async (pacienteId) => {
    const res = await fetch(`/api/pacientes/${pacienteId}`, {
      method: "DELETE"
    });
    const json = await res.json();
    if (json.acknowledged) {
      setPacientes(currentPacientes => {
        return currentPacientes.filter((currentPaciente) => currentPaciente._id !== pacienteId);
      });
    }
  };


  const updatePaciente = async (pacienteId, nuevosDatos) => {
    
    try{
    const res = await fetch(`/api/pacientes/${pacienteId}`, {
        method: "PUT",
        body: JSON.stringify(nuevosDatos),
        headers: {
            "Content-Type": "applicacion/json"
        },

    });

    const json = await res.json();
    if (json.acknowledged) {
        setPacientes(currentPacientes => {
            return currentPacientes.map((currentPaciente) => {
                if (currentPaciente._id === pacienteId) {
                    return { ...currentPaciente, ...nuevosDatos };
                }
                return currentPaciente;
            });

        });
    }
  }catch (error){
    console.log("Error al actualizar el paciente:", error);
  }
};

  return (
    <div>
    {console.log("p=", pacientes)}
      {pacientes && pacientes.length > 0 ?
        pacientes.map(p => {
          return (
            <article key={p._id} className="patient-item">
              <p className="active">Estado activo:{p.active}</p>
              <p className="name">Nombre:{p.name}</p>
              <p className="gender">Género:{p.gender}</p>
              <p className="birthDate">Fecha de nacimiento:{p.birthDate}</p>
              <button className="edit" onClick={() => updatePaciente(p._id)}>Editar</button>
              <button className="delete" onClick={() => deletePaciente(p._id)}>Borrar</button>
            </article>
          );
        }) : 
        <h2>No hay pacientes para mostrar</h2>
      }
    </div>
  );
};
