import React, { useEffect, useState } from "react";

export const ListObservations = ({ pacienteId }) => {


    return (
        <div>

            <ul>
                {observations.map(observation => (
                    <li key={observation._id}>
                        {/* Aquí puedes mostrar los detalles de cada observación */}
                        <p>{observation.descripcion}</p>
                        <p>{observation.fecha}</p>
                        {/* Agrega más campos según la estructura de tus observaciones */}
                    </li>
                ))}
            </ul>
        </div>
    )

}

export default ListObservations;

