               value={content}
               onChange={(e) => setContent(e.target.value)}
               placeholder="Enter a new patient" required
               className="form_input"
        />
        <button type="submit">Create paciente</button>

      </form>
      <div className="pacientes">
        {(pacientes.length > 0) && 
        pacientes.map((paciente) => (
          <Paciente key={paciente._id} paciente={paciente} setPacientes={setPacientes}/>

      ))}
      </div>
    </main>
    */}

}
