export default function Paciente(props) {
    const { paciente, setPacientes } = props;

    const updatePaciente = async (pacienteId, pacienteStatus) => {
        const res = await fetch(`/api/pacientes/${pacienteId}`, {
            method: "PUT",
            body: JSON.stringify({ status: pacienteStatus }),
            headers: {
                "Content-Type": "applicacion/json"
            },

        });

        const json = await res.json();
    };

    return (
        <div className="paciente">
            <p>{paciente.name} Genero:{paciente.gender}</p>
            <div>
                <button className="paciente_status"
                    onClick={() => updatePaciente(paciente._id, paciente.status)}>
                    {(paciente.status) ? "T" : "F"}
                </button>
            </div>
        </div>
    )
}
