import React, { useEffect, useState } from "react";

export const ListObservations = ({ paciente, onClose }) => {

    const [observations, setObservations] = useState([]);
  const [filteredObservations, setFilteredObservations] = useState([]);

  useEffect(() => {
    const fetchObservations = async () => {
      try {
        const response = await fetch('/api/observations');
        const data = await response.json();
        setObservations(data);
      } catch (error) {
        console.error('Error fetching observations:', error);
      }
    };

    fetchObservations();
  }, []);

  useEffect(() => {
    if (observations.length > 0 && paciente) {
      const filtered = observations.filter(obs => obs.subject === paciente._id);
      setFilteredObservations(filtered);
    }
  }, [observations, paciente]);

  return (

    <div className="container mt-5 mb-5">
    <table className="table table-striped" id="userTable">
      <thead>
        <th>Fecha</th>
        <th>Interpretación</th>
        <th>Código</th>
        <th>Valor</th>
        <th>Estado</th>
        <th>Categoría</th>
      </thead>
    </table>


    </div>

);
};

    {/*
    <div>
      <h2>Observaciones del Paciente</h2>
      
      <button onClick={onClose}>Cerrar</button>
      
      {filteredObservations.length > 0 ? (
        <ul>
          {filteredObservations.map(obs => (
            <li key={obs._id} className="patient-item">
             
              <p>Fecha: <br />{new Date(obs.effectiveDateTime).toLocaleDateString('es-ES')}</p>
              <p>Interpretación: <br />{obs.interpretation.text}</p>
              <p>Código: <br />{obs.code.text}</p>
              <p>Valor: <br />{obs.valueString}</p>
              <p>Estado: <br />{obs.status}</p>
              <p>Categoría: <br />{obs.category.text}</p>
            </li>
            
          ))}
          
        </ul>
       
      ) : (
        <p>No hay observaciones para este paciente.</p>
      )}
      </div>
      */}


export default ListObservations;

