import { useEffect, useState } from "react";
import Paciente from "./Paciente";

export default function App() {


  const [pacientes, setPacientes] = useState([]);
  const [content, setContent] = useState("");

  useEffect(() => {
    async function getPacientes() {
      const res = await fetch("/api/pacientes");
      const pacientes = await res.json();
      setPacientes(pacientes);

    }
    getPacientes();

  }, []);

  const createNewPatient = async (e) => {
    e.preventDefault();
    if(content.length > 3){
      const res = await fetch("api/pacientes", {
        method: "POST",
        body: JSON.stringify({paciente: content}),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const newPatient = await res.json();

      setContent("");
      setPacientes([...pacientes, newPatient]);
    }
  }



  return (
    <main className="container">
      <h1 className="title">Listado pacientes</h1>
      <form className="form" onSubmit={createNewPatient}>
        <input type="text" 
               value={content}
               onChange={(e) => setContent(e.target.value)}
               placeholder="Enter a new patient" required
               className="form_input"
        />
        <button type="submit">Create paciente</button>

      </form>
      <div className="pacientes">
        {(pacientes.length > 0) && 
        pacientes.map((paciente) => (
          <Paciente key={paciente._id} paciente={paciente} setPacientes={setPacientes}/>

      ))}
      </div>
    </main>
  );
}
