                      <td>{new Date(obs.effectiveDateTime).toLocaleDateString('es-ES')}</td>
                      <td>{obs.interpretation?.text || 'N/A'}</td>
                      <td>{obs.code?.text || 'N/A'}</td>
                      <td>{obs.valueString || 'N/A'}</td>
                      <td>{obs.status}</td>
                      <td>{obs.category?.text || 'N/A'}</td>
