import React from 'react'
import { useEffect, useState } from 'react'
//import { MongoClient } from 'mongodb'

export const ListPatients = (props) =>  {
  const { paciente, setPacientes } = props;

  //Creamos un estado para poder listar todas los pacientes que tenga el array del localstorage
  //este estado inicialmente estara vacio por eso se ponen corchetes vacios
  const [listadoState, setListadoState] = useState([]);
 /*
   //vamos a usar el hook use effect pq es lo que se va a ejecutar nada mas cargar el componente
  //Por eso la lista vacia como segundo componente
  useEffect(() => {
    getPatient();
  }, []);

  const getPatient = () => {
    //el objeto que recogemos lo pasamos a un objeto usable, por eso hacemos JSON.parse
    let patients = JSON.parse(localStorage.getItem("patients")); //FETCH
    //guardamos los pacientes sacados del array del localstorage en el estado
    setListadoState(patients);
  }
*/
/*
  const updatePaciente = async (pacienteId, pacienteStatus) => {
    const res = await fetch(`/api/pacientes/${pacienteId}`, {
        method: "PUT",
        body: JSON.stringify({ status: pacienteStatus }),
        headers: {
            "Content-Type": "applicacion/json"
        },

    });
*/
/*
    //Creo que este codigo es para el caso del video
    //hay un checkbox al lado de cada paciente para hacer tick o quitarlo
    //cambia la propiedad status de true a false
    const json = await res.json();
    if (json.acknowledged) {
        setPacientes(currentPacientes => {
            return currentPacientes.map((currentPaciente) => {
                if (currentPaciente._id === pacienteId) {
                    return { ...currentPaciente, status: !currentPaciente.status }
                }
                return currentPaciente;
            })

        })
    }
  
};

*/
  const deletePaciente = async (pacienteId) => {
    const res = await fetch(`api/pacientes/${pacienteId}`, {
        method: "DELETE"
    });
    const json = await res.json();
    if(json.acknowledged){
        setPacientes(currentPacientes => {
            return currentPacientes
            .filter((currentPaciente) => (currentPaciente._id !== pacienteId));
        })
    }
}

 
    
  return (

    
    <>
    
    
     {paciente != null ?
            paciente.map(p => {
              return(
                <article key={paciente._id} className="patient-item">
                      <p className="active">{paciente.active}</p>
                      <p className="name">{paciente.name}</p>
                      <p className="gender">{paciente.gender}</p>
                      <p className="birthDate">{paciente.birthDate}</p>

                      <button className="edit">Editar</button>
                      <button className="delete" onClick={() => deletePaciente(paciente._id)}>Borrar</button>
                  </article>

              );
            })
        : <h2>No hay pacientes para mostrar</h2>
    } 
    </>
  )



}
